mapper.select:
select

from RBAC_MENU_INFO
where 1=1

  and ID_MENU = #{idMenu,jdbcType=VARCHAR}


  and NAM_MENU = #{namMenu,jdbcType=VARCHAR}


  and COD_PARNT_MENU = #{codParntMenu,jdbcType=VARCHAR}


  and COD_MENU_STATE = #{codMenuState,jdbcType=DECIMAL}


  and VLU_MENU_SEQ = #{vluMenuSeq,jdbcType=DECIMAL}


  and COD_MENU_TYPE = #{codMenuType,jdbcType=VARCHAR}


  and TXT_MENU_URL = #{txtMenuUrl,jdbcType=VARCHAR}


  and TXT_MENU_DSCR = #{txtMenuDscr,jdbcType=VARCHAR}


  and VLU_MENU_ICON = #{vluMenuIcon,jdbcType=VARCHAR}


  and IND_DLGT = #{indDlgt,jdbcType=VARCHAR}


  and code = #{code,jdbcType=VARCHAR}
